AND tab.TABLE_NAME in (
#{tableName}
)
GROUP BY tab.TABLE_NAME, tab.TABLE_COMMENT, col.COLUMN_NAME, col.DATA_TYPE, col.COLUMN_COMMENT, col.COLUMN_KEY, col.ORDINAL_POSITION ORDER BY tab.TABLE_NAME, col.ORDINAL_POSITION
